<script>
document.addEventListener('DOMContentLoaded', function() {
  // Use | json para string JS segura (sem &amp;)
  var rawTitle   = {{ product.title | json }};
  var sku        = {{ sku | json }};
  var selVolume  = document.getElementById('hana-pro-volume');
  var inputVol   = document.getElementById('hana-pro-volume-custom');
  var selFreq    = document.getElementById('hana-pro-frequency');
  var btn        = document.getElementById('hana-pro-whats-btn');

  // Extrai apenas o "título base" antes de sufixos (—, -, |, :)
  function baseProductTitle(t){
    if(!t) return '';
    // normaliza hífens longos e separadores comuns
    var separators = [' — ', ' – ', ' | ', ' - ', ': '];
    var cut = t;
    for (var i=0;i<separators.length;i++){
      var idx = cut.indexOf(separators[i]);
      if (idx > -1){ cut = cut.slice(0, idx); break; }
    }
    return cut.trim();
  }
  var productTitleBase = baseProductTitle(rawTitle);

  function detectMobile(){ return /android|iphone|ipad|ipod/i.test(navigator.userAgent); }
  function currentVolume(){
    var v = selVolume.value === 'custom' ? parseInt(inputVol.value, 10) : parseInt(selVolume.value, 10);
    return isNaN(v) || v < 1 ? null : v;
  }

  function buildMessage(){
    var vol  = currentVolume() || '—';
    var freq = selFreq.value || 'Mensal';
    var text =
      "Olá, sou profissional e tenho interesse em CONDIÇÃO PROFISSIONAL / CONTRATO DE FORNECIMENTO.\n\n" +
      "Produto: " + productTitleBase + "\n" +
      "Quantidade mensal desejada: " + vol + " un\n" +
      "Frequência: " + freq + "\n" +
      "SKU: " + sku + "\n\n" +
      "Necessito: preço por volume, estabilidade de abastecimento e prazos garantidos.\n" +
      "Nome/Razão Social:\n" +
      "CNPJ/CPF:\n" +
      "Cidade:\n" +
      "UF:\n" +
      "CEP:";
    return encodeURIComponent(text);
  }

  function updateLink(){
    var base = detectMobile()
      ? "https://api.whatsapp.com/send?phone=15558043916&text="
      : "https://web.whatsapp.com/send?phone=15558043916&text=";
    btn.setAttribute('href', base + buildMessage() + "&utm_source=produto&utm_medium=whatsapp&utm_campaign=pro-volume");
  }

  // UI: exibe campo custom quando necessário
  selVolume.addEventListener('change', function(){
    var isCustom = this.value === 'custom';
    inputVol.style.display = isCustom ? 'block' : 'none';
    if (isCustom) inputVol.focus();
  });

  ['change','input'].forEach(function(evt){
    selVolume.addEventListener(evt, updateLink);
    inputVol.addEventListener(evt, updateLink);
    selFreq.addEventListener(evt, updateLink);
  });
  updateLink();
});
</script>
