<script>
  document.addEventListener('DOMContentLoaded', function() {
    var productTitle = "{{ product.title | escape }}";
    var message = "Gostaria de mais informa√ß√µes do " + productTitle + ".";
    var isMobile = /android|iphone|ipad|ipod/i.test(navigator.userAgent);
    var prefix = isMobile
      ? "whatsapp://send?phone=551147220101&text="
      : "https://web.whatsapp.com/send?phone=551147220101&text=";
    var fullLink = prefix + encodeURIComponent(message);

    var btn = document.getElementById('whatsapp-info-btn');
    btn.setAttribute('href', fullLink);
  });
</script>

